import React, {useState} from "react";
import PropTypes from "prop-types";
import "../css/components/tabs.css";

export const UnderlinedTabs = ({tabs}) => {
    const [openTab, setOpenTab] = useState([
        {key: 1, content: "1일 짜리 그래프 그릴거야", title: "1일"},
        {key: 2, content: "1주일 짜리 그래프 그릴거야", title: "1주"},
        {key: 3, content: "1 짜리 그래프 그릴거야", title: "1달"},
        {key: 4, content: "3달 짜리 그래프 그릴거야", title: "3달"},
        {key: 5, content: "6달 짜리 그래프 그릴거야", title: "6달"},
        {key: 6, content: "1년 짜리 그래프 그릴거야", title: "1년"},
        {key: 7, content: "5년 짜리 그래프 그릴거야", title: "5년"},
        {key: 8, content: "10년 짜리 그래프 그릴거야", title: "10년"}
    ])
    return (
        <div className="flex flex-wrap flex-col w-full tabs">
            <div className="flex lg:flex-wrap flex-row lg:space-x-2">
                {tabs.map((tab, key) => (
                    <div key={key} className="flex-none">
                        <button
                            onClick={() => {
                                setOpenTab(tab.index)
                            }}
                            className={
                                openTab === tab.index
                                    ? 'tab tab-underline tab-active'
                                    : 'tab tab-underline'
                            }
                            type="button">
                            {tab.title}
                        </button>
                    </div>
                ))}
            </div>
            {tabs.map((tab, key) => (
                <div
                    key={key}
                    className={`tab-content ${
                        openTab !== tab.index ? 'hidden' : 'block'
                    }`}>
                    {tab.content}
                </div>
            ))}
        </div>
    )
}

UnderlinedTabs.propTypes = {
    tabs: PropTypes.arrayOf(
        PropTypes.shape({
            index: PropTypes.number,
            content: PropTypes.element,
            title: PropTypes.any
        })
    ).isRequired
}

export default UnderlinedTabs;
