import React, { useState } from "react";
import ReactQuill from 'react-quill';
import './styles.css';
import PropTypes from 'prop-types';

export const CircularBadge = ({size = 'default', color, children}) => {
    let css = []
    css.push(color)
    css = css.join(' ')

    return (
        <span className={`badge badge-circle badge-${size} ${css}`}>
      {children}
    </span>
    )
}

export const CustomToolbarExample = () => {
    const [value, setValue] = useState('')
    const modules = {
        toolbar: [
            //[{header: [1, 2, false]}],
            ['bold', 'italic', 'underline', 'strike', 'link', 'image']
            //[{list: 'ordered'}, {list: 'bullet'}, {indent: '-1'}, {indent: '+1'}],
            //['clean']
        ]
    }

    const formats = [
        //'header',
        'bold',
        'italic',
        'underline',
        'strike',
        /*
        'blockquote',
        'list',
        'bullet',
        'indent',
        */
        'link',
        'image'
    ]
    return (
        <div className="w-full">
            <ReactQuill
                theme="snow"
                value={value}
                onChange={setValue}
                modules={modules}
                formats={formats}
            />
        </div>
    )
}

const OpinionRegister = () => {

    const [value, setValue] = useState("sample")
    const size = 'default'
    const color = 'blue'
    const children = '등록하기'
    const props = ''

    return <>
        <table>
            <tr>
                <td><h2 className={"text-2xl"}>게시글 작성하기</h2></td>
                <td>
                    <button
                        className={`btn btn-circle btn-circle-${size} 
                                btn-flat btn-flat-${color}`}{...props}>
                        {children}
                    </button>
                    <button
                    className={`btn btn-circle btn-circle-${size} 
                                btn-flat btn-flat-${color}`}{...props}>
                    {children}
                    </button>
                </td>
            </tr>
        </table>
        <div className="w-full mb-4">
            <div className="w-full">
                <ReactQuill theme="snow" value={value} onChange={setValue} />
            </div>
        </div>
        <CircularBadge color={"blue"} size={"default"} children={"태그하기"}/>
    </>
}

OpinionRegister.propTypes = {
    size: PropTypes.oneOf(['sm', 'default', 'lg']),
    flat: PropTypes.bool,
    raised: PropTypes.bool,
    color: PropTypes.string,
    children: PropTypes.any,
}
CircularBadge.propTypes = {
    size: PropTypes.oneOf(['sm', 'default', 'lg']),
    color: PropTypes.string,
    children: PropTypes.any
}

export default OpinionRegister;
