import React, { useState } from "react";
import ReactQuill from 'react-quill';
import './styles.css';
import PropTypes from 'prop-types';

export const CircularBadge = ({size = 'default', color, children}) => {
    let css = []
    css.push(color)
    css = css.join(' ')

    return (
        <span className={`badge badge-circle badge-${size} ${css}`}>
      {children}
    </span>
    )
}
export const CircularButton = ({size = 'default', color = 'blue', outlined = false,
                                   flat = false, raised = false, children, props}) => {
    let css = []
    if (raised) css.push('btn-raised')
    return (
        <button
            className={`btn btn-circle btn-circle-${size} btn-${color} ${css.join(
                ' '
            )}`}
            {...props}>
            {children}
        </button>
    )
}


const OpinionRegister = () => {
    const [value, setValue] = useState("sample")

    return <>
        <table>
            <tr>
                <td><h2 className={"text-2xl"}>게시글 작성하기</h2></td>
                <td>
                    <CircularButton children={"돌아가기"}/>
                    <CircularButton children={"등록하기"}/>
                </td>
            </tr>
        </table>
        <div className="w-full mb-4">
            <div className="w-full">
                <ReactQuill theme="snow" value={value} onChange={setValue} />
            </div>
        </div>
        <p className={"text-sm"}>태그 작성하기</p>
        <input className={"form-input"} placeholder={"키워드 입력"} type="text"/>
    </>
}

OpinionRegister.propTypes = {
    size: PropTypes.oneOf(['sm', 'default', 'lg']),
    flat: PropTypes.bool,
    raised: PropTypes.bool,
    color: PropTypes.string,
    children: PropTypes.any,
}
CircularButton.propTypes = {
    size: PropTypes.oneOf(['sm', 'default', 'lg']),
    flat: PropTypes.bool,
    raised: PropTypes.bool,
    color: PropTypes.string,
    children: PropTypes.any,
}

export default OpinionRegister;
